import '../css/Post.css';
import { useEffect, useState, useContext } from 'react'
import { useParams, useNavigate } from 'react-router-dom'
import axios from 'axios';
import EditIcon from '@mui/icons-material/Edit';
const { AuthContext } = require("../helpers/AuthContext")

function Post() {
    let { id } = useParams();
    const [postObject, setPostObject] = useState({});
    const [editTitle, setEditTitle] = useState(false)
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');
    const { authState } = useContext(AuthContext)

    let navigate = useNavigate()

    useEffect(() => {
         axios.get(`http://localhost:3001/posts/${id}`).then((response) => {
            setPostObject(response.data)
        })
        axios.get(`http://localhost:3001/comments/${id}`).then((response) => {
            setComments(response.data)
        })
    }, [id]);

    const addNewComment = () => {
        axios.post('http://localhost:3001/comments', {
            commentBody: newComment,
            PostId: id
        }, {
            headers: {
                accessToken: localStorage.getItem("accessToken")
            } 
        })
        .then((response) => {
            if (response.data.error) {
                console.log(response.data.error)
            } else {
                setComments([...comments, { id: response.data.id, commentBody: response.data.commentBody, username: response.data.username }]);
                setNewComment('');
            }
        })
    }

    const deleteComment = (id) => {
        axios.delete(`http://localhost:3001/comments/${id}`, { 
            headers: {
                accessToken: localStorage.getItem("accessToken")
            }
        }).then(() => {
            setComments(comments.filter(val => {
                return val.id !== id;
            }))
        })
    }

    const deletePost = (id) => {
        axios.delete(`http://localhost:3001/posts/${id}`, { 
            headers: {
                accessToken: localStorage.getItem("accessToken")
            }
        }).then((response) => {
            navigate('/')
        })
    }

    const editButtonClicked = () => {
        setEditTitle(true)
    }

    return (
        <div className="individualPost__container">
            <div className="individualPost__left">
                <div className="title__container">{!editTitle ?  (
                    <div className='individualPost__title'>
                        <div className='title__text'>
                            {postObject.title}
                        </div>
                        <div className='editButton'>
                            <EditIcon onClick={editButtonClicked}></EditIcon>
                        </div>
                    </div>
                )
                : (
                <div className='title__edit'>
                    <input type='text' placeholder='Edit Post' />
                    <button style={{ width: '60px', height: '30px', border: 'none' }}>Update</button>
                </div>
                )}
                </div>
                <div className="individualPost__body">{postObject.postText}</div>
                <div className="individualPost__footer">
                    <div className='username'>
                        {postObject.username}
                    </div>
                    <div className="button">{authState.username === postObject.username && (
                            <button style={{ width: '100px', height: '45px' }} onClick={() => {
                            deletePost(postObject.id)
                        }}>Delete Post</button>
                    )}
                    </div>
                </div>
            </div>
            <div className="comment__right">
                <div className='addComment__container'>
                    <input value={newComment} type="text" placeholder="Comment..." autoComplete='off' onChange={(event) => { setNewComment(event.target.value) }} />
                    <button onClick={addNewComment}>Add Comment</button>
                </div>
                <div className='listOfComments'>
                    {comments.map((comment, key) => { 
                        return (
                            <div className='commentBody'>{comment.commentBody}
                            <label>Username: {comment.username}</label>
                            {authState.username === comment.username && (
                                <button onClick={() =>{ deleteComment(comment.id) }}>Delete</button>
                            )}
                        </div>
                        )
                    })}
                </div>
            </div>
        </div>
    )
}

export default Post
